var mix = {
    methods: {
        submitPayment () {
            this.postData('/api/payment/', {
                name: this.name,
                year: this.year,
                month: this.month,
                code: this.code,
                card: this.card
            }).then(() => {
                alert('Успешная оплата')
                this.name = ''
                this.year = ''
                this.month = ''
                this.code = ''
                this.card = ''
            }).catch(() => {
                console.warn('Ошибка при оплате')
            })
        }
    },
    data () {
        return {
            card: '',
            month: '',
            year: '',
            name: '',
            code: '',
        }
    }
}